clear,clc[d,hdr]=readsgy('model_data.sgy');[nx,nt]=size(d);noise_ratio=0.5;% d=(d-min(d(:)))/(max(d(:))-min(d(:)))*2-1;% figure;imagesc(loog(d'))% noise=rand([nx,nt])*noise_ratio*2-1;% d1=noise+d;bp=randperm(nx*nt);d1=d;noise=zeros(size(d1));noise(bp(1:round(noise_ratio*bp(nx*nt))))=rand(1,round(noise_ratio*bp(nx*nt)))*2-1;% d1=d+noise*max(abs(d(:)));d1=d+noise;% max(abs(d(:)));figure;imagesc(loog(d1'))writesgy(d1,'model_data_n.sgy');% missing_ratio=0.4;% % outfile=[file,num2str(missing_ratio*10),ext];% randPool=randperm(nx);% ra=randPool(1:round(nx*missing_ratio));% msk=ones(nx,nt);% for i=1:nx%    msk(ra,:)=0;% end% d=d.*msk;